```
let UserModel = require('../../../core/models').user()  // 获取刚才的用户模型的数据库连接
let {
    GraphQLSchema,
    GraphQLObjectType,
    GraphQLString,
    GraphQLList,
    GraphQLInt,
    GraphQLNonNull,
    GraphQLBoolean,
} = require('graphql')

// 创建用户模型对应的graphql类型
let UserType = new GraphQLObjectType({
    name: 'user',
    description: 'user model',
    fields: {
        id: {
            type: GraphQLString,
            resolve: o => o['_id']
        },
        nickname: {
            type: GraphQLString,
            resolve: o => o.nickname
        },
        username: {
            type: GraphQLString,
            resolve: o => o.username
        },
        age: {
            type: GraphQLString,
            resolve: o => o.age
        }
    }
})

// 创建一个用户的集合类型
let UserListType = new GraphQLList(UserType)

// 创建graphql的Schema
let schema = new GraphQLSchema({
    query: new GraphQLObjectType({
        name: 'UsersQuery',
        description: '查询用户信息',
        fields: {
            userList: {
                type: UserListType,
                description: '用户列表',
                resolve(root, param, session) {
                    return UserModel.find()
                }
            },
            user: {
                type: UserType,
                description: '指定用户信息',
                args: {
                    id: {
                        type: GraphQLString,
                        require: true,
                    },
                },
                resolve(root, params, session) {
                    return UserModel.findOne({
                        _id: params.id
                    })
                }
            }
        }
    }),
    mutation: new GraphQLObjectType({
        name: 'UsersMutation',
        description: '修改用户信息',
        fields: {
            updateUserProfile: {
                type: GraphQLBoolean,
                description: '修改指定用户的个人资料',
                args: {
                    id: {
                        type: GraphQLString,
                        require: true,
                    },
                    terms: {
                        type: GraphQLString,
                        require: true
                    }
                },
                resolve(root, params, session) {
                    let tempModel = JSON.parse(params.terms)
                    return UserModel
                        .findOneAndUpdate({
                            _id: params.id
                        }, tempModel)
                        .then((_d) => { return !!_d })  
                }
            }
        }
    })
})

// 构造graphql的路由
let graphqlRoutes = require('express-graphql')(req => ({
    schema: schema,
    context: req.session,
    graphiql: true
}))

module.exports = graphqlRoutes
```
